using System;\nusing System.Net;\n\nclass Program {\n    static void Main() {\n        var request = WebRequest.Create("http://example.com/api/");\n        request.Credentials = CredentialCache.DefaultCredentials; // or new NetworkCredential("user", "password");\n        using (var response = request.GetResponse()) {\n            using (var streamReader = new System.IO.StreamReader(response.GetResponseStream())) {\n                Console.WriteLine(streamReader.ReadToEnd());\n            }\n        }\n    }\n}