public class MyClass\n{\n    private readonly object _lockObject = new object();\n    private bool _enabled;\n\n    public bool Enabled\n    {\n        get\n        {\n            lock (_lockObject)\n            {\n                return _enabled;\n            }\n        }\n        set\n        {\n            lock (_lockObject)\n            {\n                _enabled = value;\n            }\n        }\n    }\n\n    // Example usage in a loop\n    public void Run()\n    {\n        while (true)\n        {\n            if (!Enabled)\n            {\n                break;\n            }\n            // Perform some work\n        }\n    }\n}