using System;\nusing System.Drawing;\nusing System.IO;\nusing PdfiumViewer;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Load the PDF document from a file\n        using (var pdfDocument = PdfDocument.Load("example.pdf"))\n        {\n            // Render the first page of the document\n            using (var image = pdfDocument.Render(0, 300, 300, pdfDocument.PageSize.Width, pdfDocument.PageSize.Height, PdfRenderFlags.CorrectFromDpi))\n            {\n                // Save the rendered PDF page to a file for demonstration purposes\n                image.Save("output.png", System.Drawing.Imaging.ImageFormat.Png);\n            }\n        }\n    }\n}