using System;\nusing Microsoft.AnalysisServices.AdomdClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "Data Source=YourServer;Catalog=YourDatabase;";\n        string mdxQuery = @"SELECT [Measures].[Internet Sales Amount] ON COLUMNS, \n                                  [Date].[Calendar Year].Members ON ROWS \n                                  FROM [Adventure Works]";\n\n        using (AdomdConnection connection = new AdomdConnection(connectionString))\n        {\n            connection.Open();\n            using (AdomdCommand command = new AdomdCommand(mdxQuery, connection))\n            {\n                using (CellSet cellSet = command.ExecuteCellSet())\n                {\n                    // Process the CellSet\n                    foreach (Axis axis in cellSet.Axes)\n                    {\n                        foreach (Position position in axis.Positions)\n                        {\n                            foreach (Member member in position.Members)\n                            {\n                                Console.WriteLine(member.Caption);\n                            }\n                        }\n                    }\n\n                    // Display values from cells\n                    foreach (Cell cell in cellSet.Cells)\n                    {\n                        Console.WriteLine(cell.Value.ToString());\n                    }\n                }\n            }\n        }\n    }\n}