try\n{\n    Process.Start(new ProcessStartInfo(filePath) { UseShellExecute = true });\n}\ncatch (Win32Exception noAssociation)\n{\n    if (noAssociation.NativeErrorCode == 1155) // ERROR_NO_ASSOCIATION\n    {\n        MessageBox.Show($"No application is associated with the file type '{Path.GetExtension(filePath)}'. Please install a suitable application or set up the file association in your system settings.");\n    }\n    else\n    {\n        throw; // Re-throw exceptions you don't explicitly handle to avoid masking issues\n    }\n}\ncatch (Exception ex)\n{\n    MessageBox.Show($"An error occurred: {ex.Message}");\n}