using System.Data.SqlClient;\n\npublic void ExecuteQuery()\n{\n    string connectionString = "your_connection_string_here";\n    \n    // Use the 'using' statement to ensure the connection is closed after use.\n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        try\n        {\n            connection.Open();\n            \n            // Create a command object and execute your SQL commands here\n            SqlCommand command = connection.CreateCommand();\n            command.CommandText = "SELECT * FROM YourTable";\n            \n            // Execute the query or command\n            using (SqlDataReader reader = command.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    // Process each row\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            // Handle any exception here\n        }\n    } // The connection is automatically closed when exiting the 'using' block\n}