using System;\nusing System.Xml.Linq;\nusing OpenPop.Mime;\nusing OpenPop.Pop3;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        using (var client = new Pop3Client())\n        {\n            // Connect to the server\n            client.Connect("your.pop3.server.address", 995, true);\n\n            // Authenticate\n            client.Authenticate("your-email@example.com", "your-password");\n\n            if (client.GetMessageCount() == 0)\n            {\n                Console.WriteLine("No messages.");\n                return;\n            }\n\n            // Get the first message in the mailbox\n            var message = client.GetMessage(1);\n\n            // Extract subject and message body\n            string messageSubject = message.Headers.Subject;\n            string messageBodyContent = string.Empty;\n\n            foreach (var part in message.MessageParts)\n            {\n                if (!part.IsAttachment &&\n                    part.ContentType.MediaType == "text" &&\n                    part.ContentType.MediaSubtype == "xml")\n                {\n                    messageBodyContent = part.GetBodyAsText();\n                    Console.WriteLine("Subject: {0}", messageSubject);\n\n                    try\n                    {\n                        // Load XML content from the message body\n                        XDocument xmlContent = XDocument.Parse(messageBodyContent);\n                        Console.WriteLine("XML Content:\n{0}", xmlContent);\n                        // Process your XML here\n                    }\n                    catch (Exception ex)\n                    {\n                        Console.WriteLine("Error parsing XML: {0}", ex.Message);\n                    }\n                }\n            }\n\n            client.Disconnect();\n        }\n    }\n}