using System;\nusing System.Data.SqlClient;\nusing System.IO;\n\npublic void RetrieveFileFromDatabase(int fileId, string outputPath)\n{\n    using (SqlConnection con = new SqlConnection(System.Web.Configuration.WebConfigurationManager.ConnectionStrings["FAQ"].ToString()))\n    {\n        con.Open();\n        using (SqlCommand cmd = new SqlCommand("SELECT FileData FROM Files WHERE ID = @id", con))\n        {\n            cmd.Parameters.AddWithValue("@id", fileId);\n\n            // Execute the command and retrieve the data\n            byte[] fileData = (byte[])cmd.ExecuteScalar();\n\n            if (fileData != null)\n            {\n                // Save the retrieved binary data to a file\n                File.WriteAllBytes(outputPath, fileData);\n            }\n            else\n            {\n                throw new Exception("File not found in database.");\n            }\n\n        }\n    }\n}