public partial class UserProfilePage : Page, IUserProfileView {\n    private IUserProfilePresenter _presenter;\n\n    public event EventHandler<UserEventArgs> UserProfileUpdated;\n\n    protected void Page_Load(object sender, EventArgs e) {\n        _presenter = new UserProfilePresenter(this);\n        if (!IsPostBack) {\n            _presenter.LoadUserProfile();\n        }\n    }\n\n    protected void SaveButton_Click(object sender, EventArgs e) {\n        // Trigger the event to notify presenter\n        var handler = UserProfileUpdated;\n        if (handler != null)\n            handler.Invoke(this, new UserEventArgs { Username = usernameTextBox.Text });\n    }\n\n    public void DisplayUserProfile(User profile) {\n        usernameTextBox.Text = profile.Username;\n    }\n}