using System.Windows.Forms;\nusing System.Drawing.Text;\n\npublic class MyForm : Form\n{\n    public MyForm()\n    {\n        this.Font = new Font("Arial", 10); // Set default font\n        this.ApplyRenderingHint();\n    }\n\n    private void ApplyRenderingHint()\n    {\n        foreach (Control control in this.Controls)\n        {\n            if (control is ToolStrip ||\n                control is MenuStrip ||\n                control is ComboBox ||\n                control is ListView ||\n                control is DataGridView)\n            {\n                continue; // Skip certain controls that handle rendering internally\n            }\n            \n            control.Font = new Font("Arial", 10); // Apply consistent font\n            var graphics = this.CreateGraphics();\n            using (graphics)\n            {\n                control.TextRenderingHint = TextRenderingHint.AntiAliasGridFit;\n            }\n        }\n    }\n}