void SpawnNewProcess()\n{\n    string fileName = GetFileName();\n    System.Diagnostics.Process proc = new System.Diagnostics.Process();\n    proc.StartInfo.FileName = fileName;\n    proc.Exited += new EventHandler(ProcessExited);\n    proc.EnableRaisingEvents = true;\n    proc.Start(); // Start the process after adding event handler to avoid race conditions\n}\n\nprivate void ProcessExited(Object source, EventArgs e)\n{ \n    // Cast the source object back to Process\n    Process exitedProcess = (Process)source;\n    \n    // Now you can access properties of the exited process\n    Console.WriteLine($"Process {exitedProcess.Id} has exited.");\n}