// First, add NuGet package reference in your project.\n// Install-Package Moq\n\nusing System;\nusing Moq;\n\npublic interface IGreetingService\n{\n    string Greet(string name);\n}\n\npublic class Greeter\n{\n    private readonly IGreetingService _greetingService;\n\n    public Greeter(IGreetingService greetingService)\n    {\n        _greetingService = greetingService;\n    }\n\n    public void PrintGreeting(string name)\n    {\n        string greeting = _greetingService.Greet(name);\n        Console.WriteLine(greeting);\n    }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Create a mock for IGreetingService\n        var mock = new Mock<IGreetingService>();\n\n        // Setup the stub behavior\n        mock.Setup(m => m.Greet(It.IsAny<string>())).Returns("Hello!");\n\n        // Use the mock in your system under test\n        var greeter = new Greeter(mock.Object);\n        \n        greeter.PrintGreeting("John");\n    }\n}