using System;\nusing System.IO;\nusing System.Security.Cryptography;\n\npublic class KeyStorageExample {\n    public static void StoreKey(byte[] privateKey) {\n        byte[] encryptedData = ProtectedData.Protect(privateKey, null, DataProtectionScope.CurrentUser);\n        File.WriteAllBytes("encrypted_key.bin", encryptedData);\n    }\n\n    public static byte[] RetrieveKey() {\n        byte[] encryptedData = File.ReadAllBytes("encrypted_key.bin");\n        return ProtectedData.Unprotect(encryptedData, null, DataProtectionScope.CurrentUser);\n    }\n}