public class Person\n{\n    public string FirstName { get; set; }\n    public string LastName { get; set; }\n    public bool IsLoaded { get; set; }\n\n    public void PrintOrder()\n    {\n        Console.WriteLine($"FirstName: {FirstName}, LastName: {LastName}, IsLoaded: {IsLoaded}");\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        var s = new Person \n        { \n            FirstName = "Micah",\n            LastName = "Martin",\n            IsLoaded = true\n        };\n\n        s.PrintOrder();\n    }\n}