using System;\nusing System.IO;\nusing System.Net;\nusing System.Web;\n\npublic class SecureProxy : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        string url = "http://www.site.com/companyname/Login.aspx?style=companyname&lang=en";\n\n        // Set up the request to the external site with credentials\n        HttpWebRequest webRequest = (HttpWebRequest)WebRequest.Create(url);\n        webRequest.Credentials = new NetworkCredential("login", "password");\n\n        using (HttpWebResponse response = (HttpWebResponse)webRequest.GetResponse())\n        using (Stream inputStream = response.GetResponseStream())\n        using (StreamReader reader = new StreamReader(inputStream))\n        {\n            context.Response.Write(reader.ReadToEnd());\n        }\n    }\n\n    public bool IsReusable => false;\n}