using System;\n\npublic class JITExample\n{\n    public static void Main()\n    {\n        // Simulate some work to trigger JIT compilation\n        for (int i = 0; i < 1000000; i++)\n        {\n            DoWork(i);\n        }\n    }\n\n    private static void DoWork(int number)\n    {\n        Console.WriteLine(number * number); // Simple computation to illustrate JIT compilation\n    }\n}