protected void ValidateTimes(object source, ServerValidateEventArgs args)\n{\n    if (DateTime.TryParse(txtStart.Text, out DateTime startTime) &&\n        DateTime.TryParse(txtFinish.Text, out DateTime finishTime))\n    {\n        startTime = new DateTime(DateTime.Now.Year, DateTime.Now.Month, DateTime.Now.Day, startTime.Hour, startTime.Minute, 0);\n        finishTime = new DateTime(DateTime.Now.Year, DateTime.Now.Month, DateTime.Now.Day, finishTime.Hour, finishTime.Minute, 0);\n\n        args.IsValid = finishTime >= startTime;\n    }\n    else\n    {\n        args.IsValid = false;\n    }\n}