using System;\nusing System.Runtime.InteropServices;\n\n[ComVisible(true)]\npublic interface ITest\n{\n    string Name { get; }\n    double Date { get; }\n}\n\n[Serializable]\n[ComVisible(true)]\npublic class Test : ITest\n{\n    public string Name { get; private set; }\n    public DateTime DateInternal { get; private set; }\n\n    public Test(string name, DateTime date)\n    {\n        Name = name;\n        DateInternal = date;\n    }\n\n    public double Date\n    {\n        get => DateTime.ToOADate(DateInternal);\n    }\n}