using System;\nusing System.Runtime.InteropServices;\nusing System.Runtime.InteropServices.ComTypes;\n\n[ComVisible(true)]\npublic interface ITest\n{\n    string Name { get; }\n    void GetDate(out FILETIME date);\n}\n\n[Serializable]\n[ComVisible(true)]\npublic class Test : ITest\n{\n    private DateTime _date;\n\n    public Test(string name, DateTime date)\n    {\n        Name = name;\n        _date = date;\n    }\n\n    public string Name { get; private set; }\n\n    public void GetDate(out FILETIME fileTime)\n    {\n        var systemTime = new SYSTEMTIME();\n        systemTime.wYear = (short)_date.Year;\n        systemTime.wMonth = (short)_date.Month;\n        systemTime.wDay = (short)_date.Day;\n        systemTime.wHour = (short)_date.Hour;\n        systemTime.wMinute = (short)_date.Minute;\n        systemTime.wSecond = (short)_date.Second;\n        systemTime.wMilliseconds = (short)_date.Millisecond;\n        \n        Marshal.SystemTimeToFileTime(ref systemTime, out fileTime);\n    }\n}