using System;\nusing System.Collections.Generic;\nusing System.Text;\n\npublic class Program {\n    public static void Main() {\n        string utf8String = "“O’Brian’s café”";\n        string asciiString = ConvertToAscii(utf8String);\n        Console.WriteLine(asciiString); // Output: "O'Brians cafe"\n    }\n\n    public static string ConvertToAscii(string input) {\n        var sb = new StringBuilder();\n        foreach (char c in input) {\n            int code = (int)c;\n            if (code < 128) {\n                sb.Append(c);\n            } else {\n                // Handle special cases\n                switch (c) {\n                    case 'é':\n                    case 'è':\n                    case 'ê':\n                        sb.Append('e');\n                        break;\n                    case 'ö':\n                    case 'ô':\n                        sb.Append('o');\n                        break;\n                    case 'à': \n                    case 'á': \n                    case 'â':\n                        sb.Append('a');\n                        break;\n                    case 'ç':\n                        sb.Append('c');\n                        break;\n                    case 'ú':\n                        sb.Append('u');\n                        break;\n                    case 'í':\n                        sb.Append('i');\n                        break;\n                    case '‘':\n                    case '’':   \n                        sb.Append('\''); // Convert smart quotes to single quote\n                        break;\n                    case '“':     \n                    case '”':    // Convert double smart quotes to double quote\n                        sb.Append('"');\n                        break;\n                    default:\n                        sb.Append('?'); // unknown char\n                        break;\n                }\n            }\n        }\n        return sb.ToString();\n    }\n}