public class UserProfilePresenter {\n    private IUserProfileView _view;\n\n    public UserProfilePresenter(IUserProfileView view) {\n        _view = view;\n        view.UserProfileUpdated += OnUserProfileUpdated;\n    }\n\n    private void OnUserProfileUpdated(object sender, UserEventArgs e) {\n        new UserProfileService().Update(e.Profile);\n        LoadUserProfile();\n    }\n\n    public void LoadUserProfile() {\n        var profile = new UserProfileService().GetUserProfile("defaultUsername"); // Add actual username logic\n        _view.DisplayUserProfile(profile);\n    }\n}