using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        // Start a new Internet Explorer process\n        Process p = new Process();\n        p.StartInfo.FileName = "iexplore.exe";\n        p.StartInfo.Arguments = "about:blank";\n        p.Start();\n\n        IntPtr mainWindowHandle = IntPtr.Zero;\n\n        // Give some time for the process to start and obtain handle\n        System.Threading.Thread.Sleep(1000); \n\n        // Enumerate all processes matching 'iexplore'\n        foreach (Process ieProcess in Process.GetProcessesByName("iexplore"))\n        {\n            if (!ieProcess.MainWindowHandle.Equals(IntPtr.Zero) && ieProcess.Id == p.Id)\n            {\n                mainWindowHandle = ieProcess.MainWindowHandle;\n                break;\n            }\n        }\n\n        // Check if we found a valid handle\n        if (mainWindowHandle.Equals(IntPtr.Zero))\n        {\n            Console.WriteLine("MainWindowHandle not found for the started process.");\n        }\n        else\n        {\n            Console.WriteLine($"Found MainWindowHandle: {mainWindowHandle}");\n        }\n\n        // Use mainWindowHandle as needed, e.g., to attach to InternetExplorer object\n    }\n}