using System;\nusing System.Windows.Forms;\n\npublic class MyForm : Form\n{\n    private Label myLabel;\n\n    public MyForm()\n    {\n        myLabel = new Label();\n        myLabel.Text = "~"; // This should display the normal tilde character.\n        myLabel.AutoSize = true; // Ensures the label size adjusts to fit the text.\n        this.Controls.Add(myLabel);\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new MyForm());\n    }\n}