using System;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        DateTime dateTime = DateTime.Now;\n        string formattedDate = dateTime.ToString("yyyy-MM-ddTHH:mm:ss");\n\n        using (SqlConnection connection = new SqlConnection("your_connection_string_here"))\n        {\n            string sqlQuery = "INSERT INTO YourTable (YourDateTimeColumn) VALUES (@DateTimeValue)";\n            SqlCommand command = new SqlCommand(sqlQuery, connection);\n\n            // Use a parameter to avoid SQL injection\n            command.Parameters.AddWithValue("@DateTimeValue", formattedDate);\n\n            connection.Open();\n            command.ExecuteNonQuery();\n        }\n    }\n}