using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        Stopwatch stopwatch = new Stopwatch();\n        \n        // Start timing\n        stopwatch.Start();\n\n        DoSomeTask();\n\n        // Stop timing\n        stopwatch.Stop();\n\n        // Write the result\n        Console.WriteLine("Time taken: {0}", stopwatch.Elapsed);\n    }\n\n    private static void DoSomeTask()\n    {\n        // A method to profile\n        for (int i = 0; i < 1000; i++)\n        {\n            Console.Write(".");\n        }\n    }\n}