using System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static void Main()\n    {\n        List<string> list1 = new List<string> { "a", "c", "b", "d", "f", "e" };\n        List<string> list2 = new List<string> { "a", "d", "e", "f", "h" };\n\n        List<string> orderIssues = CheckOrder(list1, list2);\n\n        if (orderIssues.Count == 0)\n        {\n            Console.WriteLine("The elements in the correct relative order.");\n        }\n        else\n        {\n            Console.WriteLine("Elements with incorrect relative order:");\n            foreach (var item in orderIssues)\n            {\n                Console.WriteLine(item);\n            }\n        }\n    }\n\n    static List<string> CheckOrder(List<string> list1, List<string> list2)\n    {\n        List<string> commonInOrder = new List<string>();\n        List<string> issues = new List<string>();\n\n        foreach (var item in list2)\n        {\n            if (list1.Contains(item))\n            {\n                // Add to the order tracking list\n                commonInOrder.Add(item);\n\n                // Check if the current item maintains the relative order with previously found items\n                int indexInList1 = list1.IndexOf(item);\n                foreach (var prevItem in commonInOrder)\n                {\n                    if (list1.IndexOf(prevItem) > indexInList1 && !issues.Contains(prevItem))\n                    {\n                        issues.Add(prevItem);\n                    }\n                }\n            }\n        }\n\n        return issues;\n    }\n}