public class SystemMenu \n{\n    public int Id { get; set; } // Assuming you have some form of ID.\n    public IList<MenuItem> Items { get; private set; }\n    public int DefaultItemId { get; set; } // Instead of a MenuItem reference, store an ID\n\n    public MenuItem DefaultItem\n    {\n        get \n        {\n            return Items.FirstOrDefault(x => x.Id == DefaultItemId);\n        }\n        set\n        {\n            if (value != null)\n            {\n                DefaultItemId = value.Id;\n            }\n        }\n    }\n}\n\npublic class MenuItem\n{\n    public int Id { get; set; } // Assuming you have some form of ID.\n    public string Name { get; set; }\n    public int SystemMenuId { get; set; } // Foreign key to SystemMenu\n\n    [ForeignKey("SystemMenuId")]\n    public virtual SystemMenu Parent { get; set; }\n}