// Example Data Layer Method (legacy)\npublic DataSet GetData()\n{\n    // Existing data retrieval logic using DataSet\n}\n\n// Wrapper Method for Silverlight\npublic List<MyDataModel> GetDataForSilverlight()\n{\n    var dataSet = GetData();\n    var dataTable = dataSet.Tables[0];\n    var dataList = new List<MyDataModel>();\n\n    foreach (DataRow row in dataTable.Rows)\n    {\n        var item = new MyDataModel\n        {\n            // Map DataRow fields to properties in MyDataModel\n            Property1 = (int)row["Column1"],\n            Property2 = (string)row["Column2"]\n        };\n        \n        dataList.Add(item);\n    }\n\n    return dataList;\n}\n\n// Data Model for Silverlight\npublic class MyDataModel\n{\n    public int Property1 { get; set; }\n    public string Property2 { get; set; }\n}