using System.Net;\nusing System.Net.Mail;\n\npublic void SendEmailThroughGmail()\n{\n    var fromAddress = new MailAddress("yourgmail@gmail.com", "Your Name");\n    var toAddress = new MailAddress("recipient@example.com", "Recipient Name");\n    const string fromPassword = "YOUR_APP_PASSWORD"; // Use an App Password if 2FA is enabled\n    const string subject = "Greetings";\n    const string body = "Hello, this is a test email using Gmail SMTP.";\n\n    var smtp = new SmtpClient\n    {\n        Host = "smtp.gmail.com",\n        Port = 587,\n        EnableSsl = true,\n        DeliveryMethod = SmtpDeliveryMethod.Network,\n        UseDefaultCredentials = false,\n        Credentials = new NetworkCredential(fromAddress.Address, fromPassword)\n    };\n    using (var message = new MailMessage(fromAddress, toAddress)\n    {\n        Subject = subject,\n        Body = body\n    })\n    {\n        smtp.Send(message);\n    }\n}