-- Example of using a Script Task for reference (though not recommended over CozyRoc)\nusing System.Net;\nusing Renci.SshNet; // Add appropriate library reference\n\npublic void Main()\n{\n    var host = "ftp.example.com";\n    var username = Dts.Variables["User::Username"].Value.ToString();\n    var password = Dts.Variables["User::Password"].Value.ToString();\n\n    using (var client = new SftpClient(host, username, password))\n    {\n        try\n        {\n            client.Connect();\n\n            // Upload file\n            string localPath = @"C:\local\file.txt";\n            string remotePath = @"/remote/file.txt";\n            using (var uplFileStream = System.IO.File.OpenRead(localPath))\n            {\n                client.UploadFile(uplFileStream, remotePath);\n            }\n\n            Dts.TaskResult = (int)ScriptResults.Success;\n        }\n        catch\n        {\n            Dts.TaskResult = (int)ScriptResults.Failure;\n        }\n        finally\n        {\n            if (client.IsConnected)\n                client.Disconnect();\n        }\n    }\n}