<#@ template language="C#" debug="true" #>\n<#@ output extension=".cs" #>\n<#@ import namespace="System.Data.SqlClient" #>\n<#\n// You would need to connect to your database and read schema information here\nstring connectionString = "your_connection_string_here";\nusing (SqlConnection conn = new SqlConnection(connectionString))\n{\n    conn.Open();\n    using (SqlCommand cmd = new SqlCommand("SELECT TABLE_NAME, COLUMN_NAME FROM INFORMATION_SCHEMA.COLUMNS WHERE TABLE_TYPE='BASE TABLE'", conn))\n    {\n        using (SqlDataReader reader = cmd.ExecuteReader())\n        {\n            while (reader.Read())\n            {\n                string tableName = reader["TABLE_NAME"].ToString();\n                string columnName = reader["COLUMN_NAME"].ToString();\n#>\nusing Castle.ActiveRecord;\nnamespace YourNamespace\n{\n    [ActiveRecord]\n    public class <#= tableName #> : ActiveRecordBase<<#= tableName #>>\n    {\n        // Add properties here based on column data\n        public virtual string <#= columnName #> { get; set; }\n    }\n}\n<#\n            }\n        }\n    }\n}\n#>