using System;\nusing System.Collections.Specialized;\nusing System.Text.RegularExpressions;\nusing System.Web;\n\nnamespace YourNamespace\n{\n    public class HtmlEncodingModule : IHttpModule\n    {\n        public void Init(HttpApplication context)\n        {\n            context.BeginRequest += new EventHandler(OnBeginRequest);\n        }\n\n        private void OnBeginRequest(object sender, EventArgs e)\n        {\n            HttpContext current = ((HttpApplication)sender).Context;\n            \n            if (current.Request.HttpMethod == "POST")\n            {\n                // Use a NameValueCollection to store the encoded form data\n                var encodedFormData = new NameValueCollection();\n\n                foreach (string key in current.Request.Form.Keys)\n                {\n                    string value = current.Request.Form[key];\n                    \n                    // Encode HTML special characters\n                    string encodedValue = HttpUtility.HtmlEncode(value);\n                    \n                    // Add the encoded key-value pair to the collection\n                    encodedFormData.Add(key, encodedValue);\n                }\n                \n                // Replace the original form with the encoded one\n                current.Items["EncodedForm"] = encodedFormData;\n            }\n        }\n\n        public void Dispose()\n        {\n        }\n    }\n}