using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Data.OleDb;\n\npublic void ExportToAccess()\n{\n    // Connection strings for SQL Server and Access\n    string sqlConnectionString = "Data Source=mySqlServer;Initial Catalog=myDatabase;Integrated Security=True";\n    string accessConnectionString = @"Provider=Microsoft.ACE.OLEDB.12.0;Data Source=C:\path\to\mydatabase.accdb";\n\n    using (SqlConnection conSql = new SqlConnection(sqlConnectionString))\n    {\n        conSql.Open();\n        using (OleDbConnection conAccess = new OleDbConnection(accessConnectionString))\n        {\n            conAccess.Open();\n\n            // SQL command to fetch data\n            string sqlQuery = "SELECT * FROM MyTable";\n            SqlDataAdapter sda = new SqlDataAdapter(sqlQuery, conSql);\n\n            // Create a DataTable to hold the data\n            DataTable dataTable = new DataTable();\n            sda.Fill(dataTable);\n\n            if (dataTable.Rows.Count > 0)\n            {\n                // Create an OleDbCommandBuilder object to automatically generate SQL commands for single table updates\n                using (OleDbCommandBuilder oleDbCommandBuilder = new OleDbCommandBuilder(new OleDbDataAdapter("SELECT * FROM MyAccessTable", conAccess)))\n                {\n                    oleDbCommandBuilder.GetInsertCommand();\n\n                    // Fill a DataSet with the Access table data\n                    OleDbDataAdapter myAccessAdapter = new OleDbDataAdapter(oleDbCommandBuilder.SelectCommand);\n                    DataTable accessDataTable = new DataTable();\n                    myAccessAdapter.Fill(accessDataTable);\n\n                    foreach (DataRow row in dataTable.Rows)\n                    {\n                        accessDataTable.ImportRow(row);\n                    }\n\n                    // Update the Access database with the new rows\n                    myAccessAdapter.Update(accessDataTable);\n                }\n            }\n        }\n    }\n}