using System;\nusing System.Data.OleDb;\n\npublic class StringCompareExample\n{\n    public static void Main()\n    {\n        string strTest = "Hash.System.Data.DataSet has.ds";\n        \n        // Preprocess strTest by removing everything between '(' and ')'\n        string processedStrTest = RemoveParenthesesContent(strTest);\n\n        string connectionString = @"Provider=Microsoft.ACE.OLEDB.12.0;Data Source=YourDatabase.accdb;";\n        using (OleDbConnection connection = new OleDbConnection(connectionString))\n        {\n            connection.Open();\n            \n            string query = "SELECT TableName FROM HashTableConfig WHERE ? LIKE '%' + Replace(ItemIdentity, '()', '') + '%'";\n            \n            using (OleDbCommand myCommand1 = new OleDbCommand(query, connection))\n            {\n                // Add parameter to safely pass the processed string\n                myCommand1.Parameters.AddWithValue("@strTest", "%" + processedStrTest.Replace("()", "") + "%");\n\n                using (OleDbDataReader reader = myCommand1.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        Console.WriteLine(reader["TableName"].ToString());\n                    }\n                }\n            }\n        }\n    }\n\n    private static string RemoveParenthesesContent(string input)\n    {\n        // Regular expression to remove all content inside parentheses\n        return System.Text.RegularExpressions.Regex.Replace(input, @"\(.*?\)", "");\n    }\n}