// Example of mapping an entity with Fluent NHibernate\npublic class InternetTvProgramMap : ClassMap<InternetTvProgram>\n{\n    public InternetTvProgramMap()\n    {\n        Id(x => x.Id).GeneratedBy.GuidComb();\n        Map(x => x.Name).Not.Nullable().Length(255);\n        Map(x => x.Channel);\n        Map(x => x.StartTime);\n        Map(x => x.EndTime);\n\n        // Example of handling inheritance if needed\n        DiscriminateSubClassesOnColumn("ProgramType");\n    }\n}