using (SqlConnection connection = new SqlConnection(connectionString))\n{\n    SqlCommand sproc = new SqlCommand("dbo.AccountExists", connection);\n    sproc.CommandType = CommandType.StoredProcedure;\n\n    // Add input parameter\n    sproc.Parameters.AddWithValue("@UserName", userName);\n\n    // Add a parameter to capture the return value\n    SqlParameter returnValueParam = sproc.Parameters.Add("Return Value", SqlDbType.Int);\n    returnValueParam.Direction = ParameterDirection.ReturnValue;\n\n    // Open connection and execute command\n    connection.Open();\n    sproc.ExecuteNonQuery();\n\n    // Get the return value\n    int result = (int)returnValueParam.Value;\n    bool accountExists = result == 1;\n\n    return accountExists;\n}