using System;\nusing System.Data;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        var connectionString = "YourConnectionString";\n        using (var conn = new Odbc.OdbcConnection(connectionString))\n        {\n            conn.Open();\n            using (var transaction = conn.BeginTransaction())\n            {\n                try\n                {\n                    var command = new Odbc.OdbcCommand { Connection = conn, Transaction = transaction };\n                    StringBuilder sb = new StringBuilder("INSERT INTO my_table (column1, column2) VALUES ");\n                    \n                    int batchSize = 100; // Adjust based on your needs and server limits\n                    for (int i = 0; i < 500; i++) // Assuming you need to insert 500 rows\n                    {\n                        if (i % batchSize == 0)\n                        {\n                            if (sb.Length > "INSERT INTO my_table (column1, column2) VALUES ".Length)\n                            {\n                                sb.Remove(sb.Length - 2, 2); // Remove the trailing comma and space before ";"\n                                command.CommandText = sb.ToString();\n                                command.ExecuteNonQuery();\n                                sb.Clear().Append("INSERT INTO my_table (column1, column2) VALUES ");\n                            }\n                        }\n\n                        int value1 = i; // Example data\n                        string value2 = "data" + i; // Example data\n\n                        sb.Append($"({value1}, '{value2}'), ");\n                    }\n\n                    if (sb.Length > "INSERT INTO my_table (column1, column2) VALUES ".Length)\n                    {\n                        sb.Remove(sb.Length - 2, 2); // Remove the trailing comma and space before ";"\n                        command.CommandText = sb.ToString();\n                        command.ExecuteNonQuery();\n                    }\n\n                    transaction.Commit();\n                }\n                catch\n                {\n                    transaction.Rollback();\n                    throw;\n                }\n            }\n        }\n    }\n}