protected void Page_Load(object sender, EventArgs e)\n{\n    if (IsPostBack && Request["__EVENTTARGET"] == "deleteButton")\n    {\n        // Perform delete action here\n        DeleteRecord();\n    }\n}\n\nprivate void DeleteRecord()\n{\n    try\n    {\n        // Your code to delete record goes here\n        ScriptManager.RegisterStartupScript(this, GetType(), "ConfirmScript", "showMessage('Record deleted successfully.');", true);\n    }\n    catch (Exception ex)\n    {\n        ScriptManager.RegisterStartupScript(this, GetType(), "ErrorScript", "showMessage('Failed to delete record: " + ex.Message.Replace("'", "\\'") + "');", true);\n    }\n}