// This is more of a conceptual sample, manual steps are usually preferred for these kinds of tasks.\nstring eulaFilePath = @"C:\path\to\your\eula.txt";\nvar setupProject = Project; // Assume this is your setup project reference in DTE context\n\nvar fileSystemEditor = setupProject.Object.UserInterface.Item("File System").Object;\nfileSystemEditor.AddFileDialogItem(fileSystemEditor.ApplicationFolder.Installer, eulaFilePath);\n\n// Set the License Agreement dialog property\nvar licenseAgreementDialog = setupProject.Object.UserInterface.Item("Install").Item("License Agreement");\nlicenseAgreementDialog.Property["TextFileName"] = $"[DialogFont]|{Path.GetFileName(eulaFilePath)}";