using System.Collections.Generic;\nusing System.Linq;\nusing System.Xml.Linq;\n\n// Assuming xmlContent is a string containing your XML data\nXDocument xdoc = XDocument.Parse(xmlContent); // Use XDocument.Load() if reading from a file\n\nIEnumerable<XElement> nodesIWantReturned = \n    from topNode in xdoc.Descendants("topNode")\n    from middleNode in topNode.Elements("middleNode").Where(m => (string)m.Attribute("filteringAttribute") == "filterValue")\n    from penultimateNode in middleNode.Elements("penultimateNode").Where(p => (string)p.Attribute("anotherFilterAttribute") == "somethingElse")\n    select penultimateNode.Element("nodesIWantReturned");\n\n// If you want all child elements under 'nodesIWantReturned' instead of the node itself, use:\nIEnumerable<XElement> allNodesUnderTarget = \n    nodesIWantReturned.SelectMany(node => node.Elements());