static int ReadKey()\n{\n    while (true)\n    {\n        ConsoleKeyInfo choice = Console.ReadKey();\n        if (char.IsDigit(choice.KeyChar))\n        {\n            // Subtract 48 to convert ASCII value ('0' is 48) to the integer value\n            int answer = Convert.ToInt32(choice.KeyChar) - 48;\n            return answer;\n        }\n        else\n        {\n            Console.WriteLine("\nSorry, you need to input a number");\n        }\n    }\n}