using System;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Check if we received the '/silent' or '-silent' command-line option\n        if (args.Length > 0 && (args[0] == "/silent" || args[0] == "-silent"))\n        {\n            // Perform any necessary actions, without launching the main form\n            Console.WriteLine("Running in silent mode.");\n            Environment.Exit(0);\n        }\n\n        // Proceed with normal application startup\n        var mainWindow = new MainForm();\n        Application.Run(mainWindow);\n    }\n}