using Microsoft.CodeAnalysis;\nusing Microsoft.CodeAnalysis.MSBuild;\n\nstatic async Task Main(string[] args)\n{\n    var workspace = MSBuildWorkspace.Create();\n    var projectPath = @"path\to\your\project.vbproj";\n    var project = await workspace.OpenProjectAsync(projectPath);\n\n    foreach (var document in project.Documents.Where(d => d.SupportsSemanticModel))\n    {\n        var semanticModel = await document.GetSemanticModelAsync();\n\n        const string fileName = "YourVBNetFile.vb";\n        if (document.Name == fileName)\n        {\n            var syntaxRoot = await document.GetSyntaxRootAsync();\n            if (syntaxRoot != null)\n            {\n                foreach (var method in syntaxRoot.DescendantNodes().OfType<Microsoft.CodeAnalysis.VisualBasic.Syntax.MethodBlockBaseSyntax>())\n                {\n                    Console.WriteLine($"Found {method.Begin.Identifier.Text} at line {method.GetLocation().GetLineSpan().StartLinePosition.Line}");\n                    // Print the full text of the function/sub\n                    Console.WriteLine(method.ToFullString());\n                }\n            }\n        }\n    }\n}