using System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        using (var outputStream = new StreamWriter("output.txt"))\n        {\n            ConcatenateFile(outputStream, "header.txt");\n            ConcatenateFile(outputStream, "content.txt");\n            ConcatenateFile(outputStream, "footer.txt");\n        }\n    }\n\n    static void ConcatenateFile(StreamWriter outputStream, string fileName)\n    {\n        using (var inputStream = new StreamReader(fileName))\n        {\n            outputStream.Write(inputStream.ReadToEnd());\n        }\n    }\n}