protected void gvResults_SelectedIndexChanged(object sender, EventArgs e)\n{\n    RMATagsReport rpt = new RMATagsReport();\n    byte[] reportBytes = rpt.GenerateReport();\n\n    // Clear all content output from the buffer stream, and stop executing the page.\n    Response.ClearContent();\n    Response.ClearHeaders();\n    Response.AddHeader("Content-Disposition", "attachment; filename=RMA_Report.pdf");\n    Response.ContentType = "application/pdf";\n    Response.BinaryWrite(reportBytes);\n    Response.Flush();\n    Response.End();\n}