using System;\nusing ReportService2010; // Ensure you reference the SSRS Web Service\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        ReportingService2010.ReportingService2010SoapClient rs = new ReportingService2010();\n        rssNamespace.TrustedUserHeader trustedUser = new rssNamespace.TrustedUserHeader();\n\n        rs.Url = "http://your-ssrs-server/ReportServer/ReportService2010.asmx";\n        rs.UseDefaultCredentials = true;\n\n        string itemPath = "/path/to/report"; // Path to the report\n        string username = @"MYDOMAIN\JimBob";\n\n        try\n        {\n            PermissionInfo[] permissions = rs.GetPermissions(itemPath, new string[] { username }));\n\n            Console.WriteLine($"Permissions for user {username} on item {itemPath}:");\n            foreach (var permission in permissions)\n            {\n                Console.WriteLine(permission.Name);\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}