using System;\nusing System.Diagnostics;\nusing System.Runtime.InteropServices;\nusing System.Threading;\n\npublic static class HighResTimer\n{\n    // Import Win32 API functions for setting timer resolution\n    [DllImport("winmm.dll", SetLastError = true)]\n    private static extern uint timeBeginPeriod(uint uMilliseconds);\n\n    [DllImport("winmm.dll", SetLastError = true)]\n    private static extern uint timeEndPeriod(uint uMilliseconds);\n\n    public static void Main()\n    {\n        // Set the system timer to 1 ms resolution\n        uint result = timeBeginPeriod(1);\n        if (result != 0)\n        {\n            Console.WriteLine("Failed to set timer period.");\n            return;\n        }\n\n        try\n        {\n            // Create a high-resolution stopwatch instance\n            Stopwatch stopwatch = new Stopwatch();\n            stopwatch.Start();\n\n            // Desired interval in milliseconds\n            int interval = 10;\n\n            while (true)\n            {\n                // Check if the elapsed time has reached the desired interval\n                if (stopwatch.ElapsedMilliseconds >= interval)\n                {\n                    // Reset the stopwatch\n                    stopwatch.Restart();\n\n                    // Your code to execute every 10 ms goes here\n                    Console.WriteLine($"Tick at {DateTime.Now.TimeOfDay}");\n                }\n            }\n        }\n        finally\n        {\n            // Restore default timer resolution when done\n            timeEndPeriod(1);\n        }\n    }\n}