using System;\nusing System.Diagnostics;\nusing System.Web.UI;\n\npublic partial class _Default : Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        int currentConnections = GetCurrentConnections();\n        Response.Write("Current Connections: " + currentConnections);\n    }\n\n    private static int GetCurrentConnections()\n    {\n        using (PerformanceCounter pc = new PerformanceCounter("Web Service", "Current Connections"))\n        {\n            return (int)pc.NextValue();\n        }\n    }\n}