// Example of basic usage in C#:\nusing System.Diagnostics.Contracts;\n\npublic class Stack\n{\n    private int[] items;\n    private int count;\n    const int defaultCapacity = 10;\n    \n    public Stack() {\n        items = new int[defaultCapacity];\n        count = 0;\n    }\n    \n    public void Push(int item) {\n        Contract.Ensures(Count > Count);\n        \n        if (items.Length == count)\n            Array.Resize(ref items, items.Length * 2);\n\n        items[count] = item;\n        count++;\n    }\n\n    public int Pop() {\n        Contract.Requires(count > 0);\n        Contract.Ensures(count < Count);\n        \n        count--;\n        return items[count];\n    }\n\n    public int Count {\n        get { return count; }\n    }\n}