using System;\nusing System.Collections.Generic;\nusing System.Globalization;\nusing System.IO;\nusing CsvHelper;\n\npublic class YourRecord\n{\n    // Define properties for each column, or handle dynamically if you don't know them in advance.\n    public string Column1 { get; set; }\n    // ...\n\n    // Example dynamic handling: you can use a dictionary if you're unsure of the number/types of columns\n    public Dictionary<string, string> Data { get; } = new Dictionary<string, string>();\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        using (var reader = new StreamReader("path/to/your/file.csv"))\n        using (var csv = new CsvReader(reader, CultureInfo.InvariantCulture))\n        {\n            // If you know column names in advance:\n            var records = csv.GetRecords<YourRecord>();\n\n            // If you don't know column names in advance:\n            var dynamicRecords = csv.GetRecords<dynamic>();\n            \n            foreach (var record in dynamicRecords)\n            {\n                Console.WriteLine(record);\n                // Process each record\n            }\n        }\n    }\n}